package steps;import io.restassured.http.ContentType;import org.hamcrest.Matcher;import org.hamcrest.core.Is;import java.util.HashMap;import static io.restassured.RestAssured.given;import static io.restassured.RestAssured.when;import static org.hamcrest.CoreMatchers.containsString;import static org.hamcrest.CoreMatchers.hasItem;import static org.hamcrest.CoreMatchers.is;import static org.hamcrest.Matchers.containsInAnyOrder;import static org.hamcrest.Matchers.stringContainsInOrder;public class BDDStyledMethod {    public static void SimpleGETPost(String postNumber){        given().contentType(ContentType.JSON).                when().get(String.format("http://localhost:3000/post/%s", postNumber)).                then().body("author", is("Yolanda"));    }    public static void PerformContainsCollection(){        given()                .contentType(ContentType.JSON)        .when()                .get(String.format("http://localhost:3000/post/"))        .then()                .body("author", containsInAnyOrder("Yolanda","Victor","Rafa")).statusCode(200);    }}